import FireAudio from './laser.mp3'

export class Ship {

    constructor() {
        this.objetElements = document.querySelector('.ship');
        this.setX(document.querySelector(".game_window").offsetWidth/2);
        this.setY(document.querySelector(".game_window").offsetHeight - 100);
        this.speed = 5;
        this.canFire = true;
        this.fireAudio = [];

    }

    //##########################  Entity position #########################################
    setX = (x) => {
        this.x = x
        this.objetElements.style.left = `${x}px`;
    }
    setY = (y) => {
        this.y = y
        this.objetElements.style.top = `${y}px`
    }

    //################################### ship Movement ###################################################

    moveRight = () => {
        this.setX(this.x + this.speed);
        console.log("right")
    }

    moveLeft = () => {
        this.setX(this.x - this.speed);
        console.log("left")
    }

    //################################### ship Fire ###################################################

    fire = ({creatBullet}) => {
        if (this.canFire){
            this.canFire = false;
            creatBullet ({
                x: this.x + 43,
                y: this.y
            });

            this.fireAudioFinction()

            setTimeout(()=>{
                this.canFire = true;
            },500);
        }

    }

    //la logique de cette fonction devra etre implementer dans une classe specal qu'on applera SFX
    fireAudioFinction = () => {
        this.fireAudio.push(new Audio(FireAudio))
        this.fireAudio.forEach( sfx => {
            sfx.play()
            setTimeout(()=> {
                this.fireAudio.splice(this.fireAudio.indexOf(sfx),1);
            },1000)
        })
    }





}
