import React, {useState, useEffect} from 'react';
import './Ship.css'
import ShipImg from './ship.png'

function Ship(props) {

    const [shipData, setShipData] = useState({
        shipElement: "",
        x: "",
        y: "",
        speed: 5
    });

    useEffect(()=>{

        //##########################  ship position #########################################

        //on charge les données de notre vaisseau apres avoir charger le DOM
        const newData = shipData
        newData.shipElement = document.querySelector(".ship");
        newData.x = document.querySelector(".game_window").offsetWidth/2;
        newData.y = document.querySelector(".game_window").offsetHeight - 100;
        setShipData(newData);
        setX(shipData.x);
        setY(shipData.y);

        //################################### ship Control ###################################################

        const keys = {
            d:false,
            q:false,
            space:false
        }

        window.addEventListener('keydown', e => {
            keys[e.key] = true;
        })
        window.addEventListener('keyup', e => {
            keys[e.key] = false;
        })

        const interval = setInterval(() => {
            if (keys['d'] && shipData.x < document.querySelector(".game_window").offsetWidth - 90 ) {
                moveRight()
            }else  if (keys['q'] &&
                shipData.x >  5) {
                moveLeft()
            }
        }, 20);

        return () => clearInterval(interval);

    },[])

    //##########################  ship position #########################################

    const setX = (x) => {
        const xPosition = shipData;
        xPosition.x = x;
        setShipData(xPosition);
        shipData.shipElement.style.left = `${x}px`;
    }
    const setY = (y) => {
        shipData.shipElement.style.top = `${y}px`
    }

    //################################### ship Control ###################################################

    const moveRight = () => {
        setX(shipData.x + shipData.speed);
        console.log("right")
    }

    const moveLeft = () => {
        setX(shipData.x - shipData.speed);
        console.log("left")
    }

    
    return (
        <>
            <img  className="ship" src={ShipImg} alt=""  />
        </>
    );
}

export default Ship;
