import bulletImg from './weapon.png'
import fireAudio from './laser.mp3'

export class Bullet   {
    constructor({x,y}) {
        this.el = document.createElement('img')
        this.el.src = bulletImg
        this.el.className = "bullet"
        this.setX(x);
        this.setY(y);
        this.speed = 5;
        this.SFX = new Audio(fireAudio);
        document.querySelector('.game_window').append(this.el);
    }

    setX = (x) => {
        this.x = x
        this.el.style.left = `${x}px`;
    }
    setY = (y) => {
        this.y = y;
        this.el.style.top = `${y}px`
    }

    moveUp = () => {
        this.setY(this.y - this.speed);
        this.SFX.play()
        this.SFX.loop = false;
        this.SFX.playbackRate = 2;
        
    }

    remove = () => {
        this.el.remove(); //supprimer du DOM
        this.el = null; //liberer de la mémoire
    }

}
