import {Entity} from "../Entity/Entity";

export class ShipClass extends Entity{

    constructor() {
        super(".ship");

    }

    //################################### ship Movement ###################################################

    moveRight = () => {
        this.setX(this.x + this.speed);
        console.log("right")
    }

    moveLeft = () => {
        this.setX(this.x - this.speed);
        console.log("left")
    }

}
