import React, {useEffect} from 'react';
import '../Component/Ship/Ship.css'
import './GameWindow.css';
import {ShipClass} from "../Component/Ship/ShipClass";
import ShipImg from "../Component/Ship/ship.png";

function GameWindow(props) {


    useEffect(()=>{
        const a = new ShipClass();
        const keys = {
            d:false,
            q:false,
            space:false
        }

        window.addEventListener('keydown', e => {
            keys[e.key] = true;
        })
        window.addEventListener('keyup', e => {
            keys[e.key] = false;
        })

        const interval = setInterval((keys) => {
            console.log(a.control(keys))
        }, 20);

        return () => clearInterval(interval);
    },[])

    return (
        <div className='game_window' >
            <img  className="ship" src={ShipImg} alt=""  />
        </div>
    );
}

export default GameWindow;
