import bulletImg from './weapon.png'

export class Bullet   {
    constructor() {
        this.el = document.createElement('img')
        this.el.src = bulletImg
        this.el.className = "bullet"
        document.body.appendChild(this.el)
        this.objetElements = document.querySelector('.bullet');
        this.setX(document.querySelector(".game_window").offsetWidth/2);
        this.setY(document.querySelector(".game_window").offsetHeight - 100);
        this.speed = 5;
    }

    setX = (x) => {
        this.x = x
        this.objetElements.style.left = `${x}px`;
    }
    setY = (y) => {
        this.objetElements.style.top = `${y}px`
    }

}
