import ShipImg from '../../Assets/ShipImg/ship.png';
import {SFX} from "../SFX";

export class Lives {
    constructor() {
        this.el = document.createElement('img')
        this.el.src = ShipImg
        this.el.className = "ship-lives"
        this.setX(document.querySelector(".game_window").offsetWidth - 40);
        this.setY(document.querySelector(".game_window").offsetHeight - 50);
        document.querySelector('.game_window').append(this.el);
        this.sfx = new SFX();
        this.lives = 3;

    }

    //##########################  Entity position #########################################
    setX = (x) => {
        this.x = x
        this.el.style.left = `${x}px`;
    }
    setY = (y) => {
        this.y = y
        this.el.style.top = `${y}px`
    }
}
