import React, {useState, useEffect} from 'react';
import './Ship.css'
import ShipImg from './ship.png'

function Ship(props) {

    const [shipData, setShipData] = useState({
        shipElement: "",
        x: "",
        y: "",
        speed: 10
    });

    const [keys, setKey] = useState({
        d:false,
        q:false,
        space: false
    })




    //##########################  ship position #########################################

    useEffect(()=>{

        //on charge les données de notre vaisseau apres avoir charger le DOM
        const newData = shipData
        newData.shipElement = document.querySelector(".ship");
        newData.x = document.querySelector(".game_window").offsetWidth/2;
        newData.y = document.querySelector(".game_window").offsetHeight - 80;
        setShipData(newData);
        setX(shipData.x);
        setY(shipData.y);

        window.addEventListener('keydown', e => {
            if (e.key === 'd') {
                const key = keys;
                key.d = true
                setKey(key)
                moveRight()
            }else if (e.key === 'q') {
                const key = keys;
                key.q = true
                setKey(key)
                moveLeft()
            }else if (e.key === ' ') {
                const key = keys;
                key.space = true
                setKey(key)
            }
        });

      



    },[])

    const setX = (x) => {
        const xPosition = shipData;
        xPosition.x = x;
        setShipData(xPosition);
        shipData.shipElement.style.left = `${x}px`;
    }
    const setY = (y) => {
        shipData.shipElement.style.top = `${y}px`
    }

    //################################### Control ###################################################


    // document.addEventListener('keydown', (event)=>{
    //     const newObj = keys;
    //     if (event.key in newObj ) {
    //         newObj [event.key] = true;
    //         setInterval(moveRight,20);
    //     }
    //     setKeys(newObj);
    //     console.log(keys)
    //
    // });
    //
    // document.addEventListener('keyup', (event)=>{
    //     const newObj = keys;
    //     if (event.key in newObj ) {
    //         newObj [event.key] = false;
    //         clearInterval(moveRight)
    //     }
    //     setKeys(newObj);
    //     console.log(keys)
    // });

    //################################## Movement ##########################################

    const moveRight = () => {
        setX(shipData.x + shipData.speed);
        console.log("right")
    }

    const moveLeft = () => {
        setX(shipData.x - shipData.speed);
        console.log("left")
    }




    
    return (
        <>
            <img  className="ship" src={ShipImg} alt=""  />
        </>
    );
}

export default Ship;
