import React, {useState,useEffect} from 'react';
import {Ship} from "../Component/Ship/Ship";
import {Bullet} from "../Component/Bullet/Bullet";
import {SFX} from "../Component/SFX";
import {Alien} from "../Component/Alien/Alien";
import './GameWindow.css';
import '../Component/Ship/Ship.css'
import '../Component/Bullet/Bullet.css'
import '../Component/Alien/Alien.css'
const Audio = new SFX();


function GameWindow(props) {

    const [toggle, setToggle] = useState(true);


    const play = () => {
        if (toggle) {
            Audio.music.play();
            Audio.music.loop = true;
        }else {
            Audio.music.pause()
        }
        setToggle(!toggle);
    }



    useEffect(()=>{

        const ship = new Ship();

        const bullets = [];
        const creatBullet = ({x,y}) => {
            bullets.push(new Bullet ({
                x,
                y
            }));
        }
        const aliens = [];
        let limite_x = document.querySelector(".game_window").offsetWidth
        let conteur = 0;

        for (let row = 0; row < 3  ; row ++) {
            for (let col = 0 ; col < 7 && conteur < limite_x  ; col++) {
                const alien = new Alien({
                    x: col * 60,
                    y: row * 60,
                });
                conteur = row * 60;
                aliens.push(alien);
            }
        }






        //################################### ship Control ###################################################

        const keys = {
            d:false,
            q:false,
            ' ':false
        }

        window.addEventListener('keydown', e => {
            keys[e.key] = true;
            //console.log(keys)
        })
        window.addEventListener('keyup', e => {
            keys[e.key] = false;
        })

        const interval = setInterval(() => {
            if (keys['d'] && ship.x < document.querySelector(".game_window").offsetWidth - 90 ) {
                ship.moveRight()

            }
            if (keys['q'] && ship.x >  5) {
                ship.moveLeft()

            }
            if (keys[' ']) {
                ship.fire({creatBullet}); //on passe une fonction a notre class
            }

            bullets.forEach(bullet => {
                bullet.moveUp();
                if (bullet.y < 0) {   //quand le tir sort de l'écran
                    bullet.remove();
                    bullets.splice(bullets.indexOf(bullet),1);//supprimer un element du tableau bullets
                }
            });


        }, 20);










        return () => clearInterval(interval);
    },[])

    return (
        <>
            <button onClick={play}>Audio</button>
            <div className='game_window' >

            </div>

        </>

    );
}

export default GameWindow;
