const INITIAL_STATE = {
    score: 0 //state initial
}

function ScoreReducer(state = INITIAL_STATE, action) {


    switch (action.type) {
        case 'INCR' : {
            return {
                ...state,  //on copie le state actuel
                score: state.score + 10  //on modifie le nouveau state actuel

            }
        }
    }

    return state;
}

export default ScoreReducer;
