import ShipImg from '../../Assets/ShipImg/ship.png';
import {SFX} from "../SFX";

export class lives {
    constructor() {
        this.el = document.createElement('img')
        this.el.src = ShipImg
        this.el.className = "ship-lives"
        this.setX(document.querySelector(".game_window").offsetWidth/2);
        this.setY(document.querySelector(".game_window").offsetHeight);
        document.querySelector('.game_window').append(this.el);
        this.sfx = new SFX();

    }
}
