import React, {useEffect} from 'react';
import './GameWindow.css';
import {Ship} from "../Component/Ship/Ship";
import ShipImg from "../Component/Ship/ship.png";
import '../Component/Ship/Ship.css'
import {Bullet} from "../Component/Bullet/Bullet";
import '../Component/Bullet/Bullet.css'
import {SFX} from "../Component/SFX";


function GameWindow(props) {

    useEffect(()=>{

        const Audio = new SFX();
        const ship = new Ship();
        const bullets = [];
        const creatBullet = ({x,y}) => {
            bullets.push(new Bullet ({
                x,
                y
            }));
        }

        Audio.music.play()




        //################################### ship Control ###################################################

        const keys = {
            d:false,
            q:false,
            ' ':false
        }

        window.addEventListener('keydown', e => {
            keys[e.key] = true;
            //console.log(keys)
        })
        window.addEventListener('keyup', e => {
            keys[e.key] = false;
        })

        const interval = setInterval(() => {
            if (keys['d'] && ship.x < document.querySelector(".game_window").offsetWidth - 90 ) {
                ship.moveRight()
                //music.play();
            }
            if (keys['q'] && ship.x >  5) {
                ship.moveLeft()
            }
            if (keys[' ']) {
                ship.fire({creatBullet}); //on passe une fonction a notre class
            }

            bullets.forEach(bullet => {
                bullet.moveUp();
                if (bullet.y < 0) {   //quand le tir sort de l'écran
                    bullet.remove();
                    bullets.splice(bullets.indexOf(bullet),1);//supprimer un element du tableau bullets
                }
            });


        }, 20);










        return () => clearInterval(interval);
    },[])

    return (
        <div className='game_window' >
            <img  className="ship" src={ShipImg} alt=""  />
        </div>
    );
}

export default GameWindow;
