export class ShipClass {

    constructor() {
        this.shipElements = document.querySelector(".ship");
        this.setX(document.querySelector(".game_window").offsetWidth/2);
        this.setY(document.querySelector(".game_window").offsetHeight - 100);
        this.speed = 5;
    }


    //##########################  ship position #########################################

    setX = (x) => {
        this.x = x
        this.shipElements.style.left = `${x}px`;
    }
    setY = (y) => {
        this.shipElements.style.top = `${y}px`
    }

    //################################### ship Movement ###################################################

    moveRight = () => {
        this.setX(this.x + this.speed);
        console.log("right")
    }

    moveLeft = () => {
        this.setX(this.x - this.speed);
        console.log("left")
    }

}
