export class Ship {

    constructor() {
        this.objetElements = document.querySelector('.ship');
        this.setX(document.querySelector(".game_window").offsetWidth/2);
        this.setY(document.querySelector(".game_window").offsetHeight - 100);
        this.speed = 5;

    }

    //##########################  Entity position #########################################
    setX = (x) => {
        this.x = x
        this.objetElements.style.left = `${x}px`;
    }
    setY = (y) => {
        this.y = y
        this.objetElements.style.top = `${y}px`
    }

    //################################### ship Movement ###################################################

    moveRight = () => {
        this.setX(this.x + this.speed);
        console.log("right")
    }

    moveLeft = () => {
        this.setX(this.x - this.speed);
        console.log("left")
    }

    //################################### ship Fire ###################################################

    fire = ({creatBullet}) => {
        creatBullet ({
            x: this.x + 43,
            y: this.y
        })
    }

}
